const request = require('request');

const options = {
  method: 'POST',
  url: 'https://sandbox-api.divit.dev/orders',
  headers: {
    'content-type': 'application/json',
    Authorization: 'Bearer REPLACE_BEARER_TOKEN'
  },
  body: {
    totalAmount: {amount: 0, currency: 'string'},
    nonrefundableAmount: {amount: 0, currency: 'string'},
    refundableAmount: {amount: 0, currency: 'string'},
    webhook_success: 'string',
    webhook_failure: 'string',
    webhook_events: 'string',
    partnerRef: 'string',
    OrderItems: [
      {
        OrderType: 'string',
        OrderData: {
          departure: {iataCode: 'string', terminal: 'string', at: 'string'},
          arrival: {iataCode: 'string', at: 'string'},
          carrierCode: 'string',
          number: 'string',
          aircraft: {code: 'string'},
          operating: {carrierCode: 'string'},
          duration: 'string',
          id: 'string',
          numberOfStops: 0,
          '': 'string'
        }
      }
    ]
  },
  json: true
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});

const request = require('request');

const options = {
  method: 'POST',
  url: 'https://sandbox-api.divit.dev/login',
  headers: {
    'content-type': 'application/json',
    Authorization: 'Bearer REPLACE_BEARER_TOKEN'
  },
  body: {client_id: 'string', client_secret: 'string'},
  json: true
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});
